    hsl(231, 40%, 3%) 0%, 
    hsl(260, 60%, 8%) 25%, 
    hsl(280, 50%, 5%) 50%, 
    hsl(300, 40%, 4%) 75%, 
    hsl(231, 40%, 3%) 100%);
