@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased;
    background: 
      radial-gradient(ellipse at top, rgba(255, 20, 147, 0.6) 0%, transparent 60%),
      radial-gradient(ellipse at bottom left, rgba(138, 43, 226, 0.7) 0%, transparent 60%),
      radial-gradient(ellipse at bottom right, rgba(255, 105, 180, 0.5) 0%, transparent 60%),
      radial-gradient(ellipse at center, rgba(75, 0, 130, 0.4) 0%, transparent 80%),
      linear-gradient(135deg, #0a0a0a 0%, #2d1460 25%, #4a1e7a 50%, #1a0d2e 75%, #000000 100%);
    background-attachment: fixed;
    color: white;
    min-height: 100vh;
    position: relative;
    overflow-x: hidden;
  }

  body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: 
      radial-gradient(3px 3px at 20px 30px, rgba(255, 20, 147, 1), transparent),
      radial-gradient(2px 2px at 40px 70px, rgba(138, 43, 226, 0.9), transparent),
      radial-gradient(2px 2px at 90px 40px, rgba(255, 105, 180, 1), transparent),
      radial-gradient(1px 1px at 130px 80px, rgba(186, 85, 211, 0.8), transparent),
      radial-gradient(3px 3px at 160px 30px, rgba(255, 20, 147, 0.9), transparent),
      radial-gradient(2px 2px at 200px 100px, rgba(255, 0, 255, 0.7), transparent),
      radial-gradient(1px 1px at 250px 60px, rgba(255, 105, 180, 0.8), transparent);
    background-repeat: repeat;
    background-size: 300px 200px;
    animation: galaxy-drift 45s linear infinite;
    pointer-events: none;
    z-index: -1;
  }

  @keyframes galaxy-drift {
    0% { transform: translateX(0) translateY(0); }
    25% { transform: translateX(-10px) translateY(-5px); }
    50% { transform: translateX(10px) translateY(-10px); }
    75% { transform: translateX(-5px) translateY(5px); }
    100% { transform: translateX(0) translateY(0); }
  }

  /* Nebula effect for metaverse feel */
  body::after {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: 
      radial-gradient(circle at 25% 25%, rgba(255, 20, 147, 0.3) 0%, transparent 40%),
      radial-gradient(circle at 75% 75%, rgba(138, 43, 226, 0.3) 0%, transparent 40%),
      radial-gradient(circle at 50% 10%, rgba(255, 105, 180, 0.4) 0%, transparent 50%),
      radial-gradient(circle at 10% 90%, rgba(186, 85, 211, 0.3) 0%, transparent 45%),
      radial-gradient(circle at 80% 20%, rgba(255, 0, 255, 0.25) 0%, transparent 35%);
    animation: nebula-pulse 12s ease-in-out infinite alternate;
    pointer-events: none;
    z-index: -2;
  }

  @keyframes nebula-pulse {
    0% { opacity: 0.3; transform: scale(1); }
    100% { opacity: 0.7; transform: scale(1.05); }
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
