import { Connection, PublicKey, ParsedTransactionWithMeta, ParsedAccountData, ParsedInnerInstruction } from '@solana/web3.js';
import { SOLANA_RPC_URL, TREASURY_WALLET, GOLDIUM_TOKEN_ADDRESS, SOLSCAN_BASE_URL } from './constants';

export interface RealTransaction {
  signature: string;
  type: 'SWAP' | 'SEND' | 'STAKE' | 'TRANSFER' | 'UNKNOWN';
  amount: number;
  tokenSymbol: string;
  fromAddress: string;
  toAddress: string;
  timestamp: Date;
  solscanUrl: string;
  success: boolean;
  fee: number;
  programId?: string;
}

const connection = new Connection(SOLANA_RPC_URL, 'confirmed');

export async function fetchRealTransactions(
  walletAddress: string = TREASURY_WALLET,
  limit: number = 20
): Promise<RealTransaction[]> {
  try {
    const publicKey = new PublicKey(walletAddress);
    console.log(`Fetching real transactions for wallet: ${walletAddress}`);

    // Get confirmed transaction signatures
    const signatures = await connection.getSignaturesForAddress(publicKey, {
      limit: limit,
      commitment: 'confirmed'
    });

    console.log(`Found ${signatures.length} transaction signatures`);

    const transactions: RealTransaction[] = [];

    // Process each transaction signature
    for (const sigInfo of signatures) {
      try {
        const txDetails = await connection.getParsedTransaction(sigInfo.signature, {
          commitment: 'confirmed',
          maxSupportedTransactionVersion: 0
        });

        if (!txDetails) {
          console.log(`Transaction details not found for: ${sigInfo.signature}`);
          continue;
        }

        const tx = parseTransactionDetails(txDetails, sigInfo.signature);
        if (tx) {
          transactions.push(tx);
        }
      } catch (txError) {
        console.error(`Error processing transaction ${sigInfo.signature}:`, txError);
        // Create minimal transaction record for failed parsing
        transactions.push({
          signature: sigInfo.signature,
          type: 'UNKNOWN',
          amount: 0,
          tokenSymbol: 'SOL',
          fromAddress: walletAddress.slice(0, 4) + '...' + walletAddress.slice(-4),
          toAddress: '???',
          timestamp: new Date((sigInfo.blockTime || Date.now() / 1000) * 1000),
          solscanUrl: `${SOLSCAN_BASE_URL}/tx/${sigInfo.signature}`,
          success: sigInfo.err === null,
          fee: 0
        });
      }
    }

    console.log(`Successfully parsed ${transactions.length} transactions`);
    return transactions;

  } catch (error) {
    console.error('Error fetching real transactions:', error);
    return [];
  }
}

function parseTransactionDetails(
  txDetails: ParsedTransactionWithMeta,
  signature: string
): RealTransaction | null {
  try {
    const message = txDetails.transaction.message;
    const meta = txDetails.meta;

    if (!meta) return null;

    const fee = meta.fee / 1_000_000_000; // Convert lamports to SOL
    const success = meta.err === null;
    const timestamp = new Date((txDetails.blockTime || Date.now() / 1000) * 1000);

    // Analyze transaction type based on instructions
    let type: RealTransaction['type'] = 'UNKNOWN';
    let amount = 0;
    let tokenSymbol = 'SOL';
    let fromAddress = '';
    let toAddress = '';

    // Check for SPL token transfers (GOLDIUM)
    const tokenTransfers = meta.postTokenBalances?.filter(balance => 
      balance.mint === GOLDIUM_TOKEN_ADDRESS
    ) || [];

    if (tokenTransfers.length > 0) {
      tokenSymbol = 'GOLD';
      type = 'TRANSFER';

      // Calculate amount from balance changes
      const preBalances = meta.preTokenBalances || [];
      const postBalances = meta.postTokenBalances || [];

      for (const postBalance of postBalances) {
        const preBalance = preBalances.find(pre => 
          pre.accountIndex === postBalance.accountIndex
        );

        if (preBalance && postBalance.uiTokenAmount && preBalance.uiTokenAmount) {
          const change = postBalance.uiTokenAmount.uiAmount! - preBalance.uiTokenAmount.uiAmount!;
          if (Math.abs(change) > amount) {
            amount = Math.abs(change);
          }
        }
      }
    } else {
      // Check SOL transfers
      const preBalances = meta.preBalances || [];
      const postBalances = meta.postBalances || [];

      for (let i = 0; i < preBalances.length; i++) {
        const change = (postBalances[i] - preBalances[i]) / 1_000_000_000;
        if (Math.abs(change) > amount && Math.abs(change) > 0.001) {
          amount = Math.abs(change);
          tokenSymbol = 'SOL';
          type = 'SEND';
        }
      }
    }

    // Determine transaction type from program instructions
    for (const instruction of message.instructions) {
      if ('programId' in instruction) {
        const programId = instruction.programId.toString();

        // Jupiter swap detection
        if (programId === 'JUP6LkbZbjS1jKKwapdHNy74zcZ3tLUZoi5QNyVTaV4' || 
            programId === 'JUP4Fb2cqiRUcaTHdrPC8h2gNsA2ETXiPDD33WcGuJB') {
          type = 'SWAP';
        }
        // Stake program detection
        else if (programId === 'Stake11111111111111111111111111111111111112') {
          type = 'STAKE';
        }
        // Token program (SPL transfers)
        else if (programId === 'TokenkegQfeZyiNwAJbNbGKPFXCWuBvf9Ss623VQ5DA') {
          type = 'TRANSFER';
        }
      }
    }

    // Get addresses
    const accounts = message.accountKeys;
    fromAddress = accounts[0]?.pubkey?.toString().slice(0, 4) + '...' + 
                  accounts[0]?.pubkey?.toString().slice(-4) || '???';
    toAddress = accounts[1]?.pubkey?.toString().slice(0, 4) + '...' + 
                accounts[1]?.pubkey?.toString().slice(-4) || '???';

    return {
      signature,
      type,
      amount: amount || 0.001, // Minimum amount for display
      tokenSymbol,
      fromAddress,
      toAddress,
      timestamp,
      solscanUrl: `${SOLSCAN_BASE_URL}/tx/${signature}`,
      success,
      fee,
      programId: message.instructions[0] && 'programId' in message.instructions[0] 
        ? message.instructions[0].programId.toString() 
        : undefined
    };

  } catch (error) {
    console.error('Error parsing transaction details:', error);
    return null;
  }
}

// Live transaction monitoring
export class TransactionMonitor {
  private connection: Connection;
  private watchedAddresses: Set<string>;
  private listeners: Map<string, (tx: RealTransaction) => void>;
  private subscriptions: Map<string, number>;

  constructor() {
    this.connection = new Connection(SOLANA_RPC_URL, 'confirmed');
    this.watchedAddresses = new Set();
    this.listeners = new Map();
    this.subscriptions = new Map();
  }

  async startMonitoring(address: string, callback: (tx: RealTransaction) => void) {
    if (this.watchedAddresses.has(address)) {
      console.log(`Already monitoring address: ${address}`);
      return;
    }

    try {
      const publicKey = new PublicKey(address);
      this.watchedAddresses.add(address);
      this.listeners.set(address, callback);

      // Subscribe to account changes
      const subscriptionId = this.connection.onAccountChange(
        publicKey,
        async (accountInfo, context) => {
          console.log(`Account change detected for ${address}`);
          // Fetch latest transactions when account changes
          const latestTxs = await fetchRealTransactions(address, 1);
          if (latestTxs.length > 0) {
            callback(latestTxs[0]);
          }
        },
        'confirmed'
      );

      this.subscriptions.set(address, subscriptionId);
      console.log(`Started monitoring ${address} with subscription ${subscriptionId}`);

    } catch (error) {
      console.error(`Error starting monitoring for ${address}:`, error);
    }
  }

  async stopMonitoring(address: string) {
    const subscriptionId = this.subscriptions.get(address);
    if (subscriptionId !== undefined) {
      await this.connection.removeAccountChangeListener(subscriptionId);
      this.subscriptions.delete(address);
      this.watchedAddresses.delete(address);
      this.listeners.delete(address);
      console.log(`Stopped monitoring ${address}`);
    }
  }

  stopAll() {
    for (const address of this.watchedAddresses) {
      this.stopMonitoring(address);
    }
  }
}

