import { 
  Connection, 
  PublicKey, 
  Transaction, 
  SystemProgram,
  LAMPORTS_PER_SOL
} from '@solana/web3.js';
import { 
  TOKEN_PROGRAM_ID,
  createTransferInstruction,
  getOrCreateAssociatedTokenAccount,
  getAssociatedTokenAddress
} from '@solana/spl-token';
import { selfContainedWallet } from './wallet-service';
import { TREASURY_WALLET, GOLDIUM_TOKEN_ADDRESS, SOL_TO_GOLD_RATE, GOLD_TO_SOL_RATE } from './constants';

export interface SwapResult {
  success: boolean;
  signature?: string;
  error?: string;
}

export interface SwapMetadata {
  timestamp: number;
  txHash: string;
  fromToken: 'SOL' | 'GOLD';
  toToken: 'SOL' | 'GOLD';
  fromAmount: number;
  toAmount: number;
  rate: number;
}

class SwapService {
  private connection: Connection;
  private swapHistory: SwapMetadata[] = [];

  constructor() {
    this.connection = selfContainedWallet.getConnection();
  }

  // Swap SOL to GOLD
  async swapSolToGold(solAmount: number): Promise<SwapResult> {
    try {
      console.log(`Swapping ${solAmount} SOL to GOLD through treasury`);

      // Check if wallet has sufficient SOL (including transaction fees)
      const currentBalance = await selfContainedWallet.getBalance();
      const feeBuffer = 0.001; // Reserve for transaction fees
      const requiredAmount = solAmount + feeBuffer;

      if (currentBalance < requiredAmount) {
        const errorMsg = `Insufficient SOL balance. Need ${requiredAmount.toFixed(6)} SOL but only have ${currentBalance.toFixed(6)} SOL`;
        console.error(errorMsg);
        return { success: false, error: errorMsg };
      }

      const treasuryPubkey = new PublicKey(TREASURY_WALLET);
      const goldAmount = solAmount * SOL_TO_GOLD_RATE;

      // Create transaction to send SOL to treasury
      const transaction = new Transaction();

      // Add SOL transfer to treasury
      transaction.add(
        SystemProgram.transfer({
          fromPubkey: selfContainedWallet.getPublicKey(),
          toPubkey: treasuryPubkey,
          lamports: solAmount * LAMPORTS_PER_SOL,
        })
      );

      // Send transaction
      const signature = await selfContainedWallet.signAndSendTransaction(transaction);

      // Record swap metadata
      const swapData: SwapMetadata = {
        timestamp: Date.now(),
        txHash: signature,
        fromToken: 'SOL',
        toToken: 'GOLD',
        fromAmount: solAmount,
        toAmount: goldAmount,
        rate: SOL_TO_GOLD_RATE
      };

      this.swapHistory.push(swapData);

      console.log(`Swap successful: ${signature}`);
      return { success: true, signature };

    } catch (error: any) {
      console.error('SOL to GOLD swap failed:', error);
      let errorMessage = error.message;

      // Provide more user-friendly error messages
      if (errorMessage.includes('Attempt to debit an account but found no record of a prior credit')) {
        errorMessage = 'Insufficient SOL balance for this transaction';
      } else if (errorMessage.includes('insufficient funds')) {
        errorMessage = 'Not enough SOL to complete this swap';
      }

      return { success: false, error: errorMessage };
    }
  }

  // Swap GOLD to SOL (simplified - in reality would need GOLD token account)
  async swapGoldToSol(goldAmount: number): Promise<SwapResult> {
    try {
      console.log(`Swapping ${goldAmount} GOLD to SOL through treasury`);

      const solAmount = goldAmount * GOLD_TO_SOL_RATE;

      // This would involve transferring GOLD tokens to treasury
      // and receiving SOL back - simplified for now

      const mockSignature = `mock_gold_to_sol_${Date.now()}`;

      // Record swap metadata
      const swapData: SwapMetadata = {
        timestamp: Date.now(),
        txHash: mockSignature,
        fromToken: 'GOLD',
        toToken: 'SOL',
        fromAmount: goldAmount,
        toAmount: solAmount,
        rate: GOLD_TO_SOL_RATE
      };

      this.swapHistory.push(swapData);

      console.log(`GOLD to SOL swap simulated: ${mockSignature}`);
      return { success: true, signature: mockSignature };

    } catch (error: any) {
      console.error('GOLD to SOL swap failed:', error);
      return { success: false, error: error.message };
    }
  }

  // Get swap history
  getSwapHistory(): SwapMetadata[] {
    return this.swapHistory;
  }

  // Clear swap history
  clearHistory(): void {
    this.swapHistory = [];
  }
}

export const swapService = new SwapService();

client/src/hooks/use-external-wallet-bal.content:
import { useQuery } from '@tanstack/react-query';
import { useExternalWallets } from './use-external-wallets';
import { stakingService } from '@/lib/staking-service';
import { splTokenService } from '@/lib/spl-token-service';

export function useExternalWalletBalances() {
  const wallet = useExternalWallets();

  return useQuery({
    queryKey: ['external-wallet-balances', wallet.address],
    queryFn: async () => {
      // Always return default values first
      const defaultBalances = {
        sol: 0,
        gold: 0,
        stakedGold: 0,
        claimableRewards: 0,
      };

      if (!wallet.connected || !wallet.publicKey) {
        return defaultBalances;
      }

      console.log('Fetching balances for external wallet:', wallet.address);

      // Get SOL balance (already available from wallet hook)
      const solBalance = wallet.balance;

      // Get staking info
      const stakeInfo = stakingService.getStakeInfo();

      // Get real GOLD balance from SPL token account
      let goldBalance = 0;
      try {
        // Fetch real SPL token balance using the wallet's public key
        goldBalance = await splTokenService.getGoldBalance(wallet.publicKey);
      } catch (error) {
        console.warn('GOLD balance fetch error:', error);
        goldBalance = 0;
      }

      return {
        sol: solBalance || 0,
        gold: goldBalance || 0,
        stakedGold: stakeInfo?.totalStaked || 0,
        claimableRewards: stakeInfo?.claimableRewards || 0,
      };
    },
    enabled: true, // Always enable, not just when connected
    refetchInterval: 15000, // Refetch every 15 seconds for real-time updates
    staleTime: 5000, // Consider data stale after 5 seconds
    initialData: {
      sol: 0,
      gold: 0,
      stakedGold: 0,
      claimableRewards: 0,
    },
  });
}
